#!/usr/bin/env node
"use strict";
const gomamayo = require('./index');
(async function () {
    const inString = process.argv[2];
    console.log(`入力文字列　　　: ${inString}`);
    const res = await gomamayo.find(inString);
    console.log('\n解析結果:');
    if (res) {
        console.log(res[0]);
        console.log('\n解析内容:');
        console.log(res[1]);
    }
    else if (res === null) {
        console.log('未検出');
    }
    else {
        console.log('エラー\nMeCabがインストールされてないかもです');
    }
}());
